            var tab = event.target;
            for (; tab && !tab.panelType; tab = tab.parentNode);

            if (tab)
            {
                // Select after a timeout to increase teh snappy
                setTimeout(FBL.bindFixed(function()
                {
                    this.selectTab(tab);
                }, this));
            }

