handlers.handler:
            if (event.keyCode ==  KeyEvent.DOM_VK_RETURN)
            {
                if (FBL.isShift(event))
                    this.searchPrev();
                else if (FBL.isControl(event))
                    FBL.dispatch([Firebug.A11yModel], 'moveToSearchMatch');
                else
                    this.searchNext();
            }
            else
                return;

            FBL.cancelEvent(event);
